package redis

import (
	"fmt"
	"testing"
	"time"
)

func BenchmarkIsPalindrome(b *testing.B) {
	SetClient(testClientName, testPptions)
	defer ClearClient()

	value := "1234567890123456"
	var expiration time.Duration = 100000
	for i := 0; i < b.N; i++ {
		StringSet(testClientName, fmt.Sprintf("%d", i), value, expiration)
	}
}
